package najah.edu;

public class product {

	private String name;
	private int price;
	public void setname(String item) {
		// TODO Auto-generated method stub
		name=item;
	}
	public void setprice(Integer p) {
		// TODO Auto-generated method stub
		price=p.intValue();
	}
	public Object getname() {
		// TODO Auto-generated method stub
		return name;
	}
	public int getprice() {
		// TODO Auto-generated method stub
		return price;
	}
}
